html, body, div, p, h1, h2, h3, h4, h5, h6, a, span, img, ol, ul, li, form, input {
    margin: 0;
    padding: 0;
    outline: 0;
    font-size: 100%;
    font-weight: normal;
    background: transparent;
    -moz-box-sizing: border-box;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
}
/* PopUp на выход курсора*/

.mouse_moved {
    position: fixed;
}

.line_top {
    width: 100%;
    height: 10px;
    top: 0;
}

.line_bottom {
    width: 100%;
    height: 10px;
    bottom: 0;
}

.line_left {
    width: 10px;
    height: 100%;
    left: 0;
    top: 0;
}

.line_right {
    width: 10px;
    height: 100%;
    right: 0;
    top: 0;
}

/* PopUp на выход курсора конец*/



/*PopUp на заказы*/
.show-message{
    font-family: 'Roboto-Regular';

}
.blink {
    color: red!important;
    animation-name: blinker;
    animation-duration: 1s;
    animation-timing-function: linear;
    animation-iteration-count: infinite;
    -webkit-animation-name: blinker;
    -webkit-animation-duration: 1s;
    -webkit-animation-timing-function: linear;
    -webkit-animation-iteration-count: infinite;
    -moz-animation-name: blinker;
    -moz-animation-duration: 1s;
    -moz-animation-timing-function: linear;
    -moz-animation-iteration-count: infinite;
    text-decoration: line-through;
}

.show-message p
{
    margin: 0!important;
    padding-left: 5px!important;
}
.show-message__icon{
    /*margin-right: 10px;*/
    width: 50px!important;
    display: inline-block;
    vertical-align: middle;
    box-sizing: border-box;
}
/*
.show-message__info{
    width: 220px;
    line-height: normal;
    display: inline-block;
    margin-left: 15px;
    color: #ffffff;
    vertical-align: middle;
    margin-bottom: 0;
    font-size: 20px;
}
*/
.show-message__info {
    width: 244px;
    line-height: normal;
    display: inline-block;
    color: #ffffff;
    vertical-align: middle;
    margin-bottom: 0;
    font-size: 19px;
    font-family: "RobotoRegular",sans-serif;
}
.show-message__info span{
    font-size: 20px;
    font-family: "RobotoRegular",sans-serif;
}
.show-message__left{
    font-size: 14px;
}
.show-message__left span{
    font-size: 15px;
}
.show-message_call{
    background-color: #363636;

}

.show-message__info span{
    color: #ffffff;
}
.package_left, .package_left span{
    font-size: 15px!important;
}
#ouibounce-modal{
    background-color: rgba(0, 0, 0, 0.9);
}
.show-message_online{
    background-color: #CD5555;
    background-color: rgba(0, 0, 0, 0.9);
}
.show-message__inner{
    line-height: 90px;
    display: inline-block;
    vertical-align: middle;
}

.show-message__item, .show-message__item-first {
    position: fixed;
    right: 20px;
    top: 120px;
    width: 318px;
    background-color: rgba(0, 0, 0, 0.9);
    color: white;
    padding: 5px;
    font-size: 14px;
    line-height: 90px;
    border-radius: 10px;
    display: none;
    z-index:98;
    
}

.lost_position{
    display: none!important;
    opacity: 0 !important;
}
.block_position{
    display: block !important;
    opacity: 1 !important;
}







@media screen and (max-width:767px){
    .show-message__item,.show-message__item-first {
        top: auto;
        right: 10px;
        bottom: 10px;
    }
    .show-message__info {
        width: 220px;
    }
    .show-message__item, .show-message__item-first {
        width: 300px;
    }
}

@media screen and (max-width:319px){
    .show-message__item,.show-message__item-first {
        width: 225px;
    }
    .show-message__info {
        width: 160px;
        font-size: 15px;
    }
    .show-message__info span {
        font-size: 17px;
    }
    .show-message__icon {
        width: 38px!important;
    }
    .show-message__info br{display: none;}
}




@-moz-keyframes blinker {
    0% { opacity: 1.0; }
    50% { opacity: 0.0; }
    100% { opacity: 1.0; }
}

@-webkit-keyframes blinker {
    0% { opacity: 1.0; }
    50% { opacity: 0.0; }
    100% { opacity: 1.0; }
}

@keyframes blinker {
    0% { opacity: 1.0; }
    50% { opacity: 0.0; }
    100% { opacity: 1.0; }
}

